import * as React from "react";
import { ReactSketchCanvas } from "react-sketch-canvas";
import { useState } from "react";
import { useRef } from "react";


const colors = ["red", "green", "yellow", "black", "blue"];

export const Draw = () => {
  const canvasRef = useRef();
  const [selectedColor, setSelectedColor] = useState(colors[0]);
  const [imageURL, setImageURL] = useState("");

  const styles = {
    border: "0.0625rem solid #9c9c9c",
    borderRadius: "0.25rem",

    height: "700px",

    objectFit: "fit",
  };
  const undoDraw = () => {
    canvasRef.current.undo();
  };

  const imageExport = async () => {
    const exportImage = canvasRef.current?.exportImage;
    if (exportImage) {
      const exportedDataURI = await exportImage("png");
      setImageURL(exportedDataURI);
      console.log(exportedDataURI);
      downloadURI(exportedDataURI, "image.png");
    }
  };
  const clearCanvas = () => {
    canvasRef.current.clearCanvas();
  };

  function downloadURI(uri, name) {
    var link = document.createElement("a");
    link.download = name;
    link.href = uri;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }

  return (
    <>
      <ReactSketchCanvas
        id="canvas"
        className="canva"
        width="600"
        height="900"
        style={styles}
        strokeWidth={4}
        strokeColor={selectedColor}

        ref={canvasRef}

      ></ReactSketchCanvas>

      <div>
        <select
          className="dropdown-menu position-static d-grid gap-1 p-2 rounded-3 mx-0 shadow"
          value={selectedColor}
          onChange={(e) => setSelectedColor(e.target.value)}
        >
          {colors.map((color) => (
            <option key={color} value={color}>
              {color}
            </option>
          ))}
        </select>
        <button className=" btn  btn-primary" onClick={undoDraw}>undo</button><p></p>
        <button className=" btn  btn-primary" onClick={clearCanvas}>clear</button><p></p>
        <button className=" btn  btn-primary" onClick={imageExport}>Download</button>
      </div>
    </>
  );
};
