import Axios, * as others from "axios";
import React, { useEffect, useState } from "react";

function UserListPage({ socket }) {
  const [userList, setUserList] = useState([]);

  const getUserList = async () => {
    Axios.get("http://localhost:3001/read").then((response) => {
      setUserList(response.data);
    });
  };

  const deleteUser = async (id) => {
    console.log("Delete" + id);
    await Axios.post(`http://localhost:3001/delete/${id}`);
  };

  useEffect(() => {
    getUserList();
  }, []);

  useEffect(() => {
    socket.on("user_added", () => {
      console.log("user_added");
      getUserList();
    });
  }, [socket]);

  return (
    <div className="container">
      <div className="row">
        <div className="col">First Name</div>
        <div className="col">Last Name</div>
        <div className="col">Email</div>
        <div className="col">Delete</div>
      </div>

      {userList.map((val, key) => {
        return (
          <div className="row" key={key}>
            <div className="col">{val.firstName}</div>
            <div className="col">{val.lastName}</div>
            <div className="col">{val.email}</div>

            <div
              className="col"
              onClick={() => {
                deleteUser(val._id);
              }}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-trash"
                viewBox="0 0 16 16"
              >
                <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z" />
                <path
                  fillRule="evenodd"
                  d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"
                />
              </svg>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default UserListPage;
